import axios from 'axios';



  var baseURL;
  axios.defaults.baseURL = "https://localhost:7222";
export default {


  getTasks: async () => {

    const result = await axios.get(`${baseURL}/items`)
    return result.data;
  },

addTask: async (name) => {
    console.log('addTask', name)
    //TODO
    await axios.post(`${baseURL}/item`, { Name: name, IsComplete: false })

    return {};
  },

setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete })
    //TODO

    return await axios.put(`${baseURL}/todoitems/` + id, id)

  },

deleteTask: async (id) => {
    console.log('deleteTask')

    return await axios.delete(`${baseURL}/todoitems/` + id, id)
  }
};
